mapper.select:
SELECT board_id, name, description
FROM board
WHERE board_id = #{boardId}

mapper.update:
UPDATE board
SET name = #{name},
description = #{description},
user_active = #{userActive},
admin_active = #{adminActive}
WHERE board_id = #{boardId}

mapper.insert:
INSERT INTO post (
board_id, author_id, password, title, content,
view_count, ip_hash, created_at, updated_at
) VALUES (
#{boardId}, #{authorId}, #{password}, #{title}, #{content},
#{viewCount}, #{ipHash}, #{createdAt}, #{updatedAt}
)

mapper.update:
UPDATE post
SET title = #{title}, content = #{content}, updated_at = #{updatedAt}
WHERE post_id = #{postId}
AND author_id = #{authorId}
AND password = #{password}

mapper.delete:
DELETE FROM post
WHERE post_id = #{postId}
AND author_id = #{authorId}
AND password = #{password}

mapper.insert:
INSERT INTO comment (
post_id, author_id, password, content,
ip_hash, created_at, updated_at
) VALUES (
#{postId}, #{authorId}, #{password}, #{content},
#{ipHash}, #{createdAt}, #{updatedAt}
)

mapper.update:
UPDATE comment
SET content = #{content}, ip_hash = #{ipHash}, updated_at = #{updatedAt}
WHERE comment_id = #{commentId}
AND author_id = #{authorId}
AND password = #{password}

mapper.delete:
DELETE FROM comment
WHERE comment_id = #{commentId}
AND author_id = #{authorId}
AND password = #{password}
